        public static byte LocationDecode(in byte Task)
        {
            switch (Task)
            {
                case 0: case 1: case 2: case 3: return 0;
                case 4: case 5: case 6: return 1;
                case 7: case 8: return 2;
                default: return 3;
            }
        }
        //[EN] Check if got wall on the way
        //[RU] Проверка на препятствия
        public static bool IsWayNext(byte map)
        {
            return (map == 0) || (map == 170) || (map >= 6 && map <= 9)
            || (map >= 104 && map <= 108) || (map >= 112 && map <= 120)
            || (map >= 150 && map <= 152) || (map >= 191 && map <= 192);
        }
        public static byte[] CheckModelCoord(in byte Condition)
        {
            for (byte i = 0; i < MapScheme.GetLength(0); i++)
                for (byte j = 0; j < MapScheme.GetLength(1); j++)
                    if (MapScheme[i, j] == Condition)
                        return new byte[] { i, j };
            return new byte[] { 0, 0 };
        }
        public static bool CheckMap(in byte Condition)
        {
            for (byte lv1 = 0; lv1 < MapScheme.GetLength(0); lv1++)
                for (byte lv2 = 0; lv2 < MapScheme.GetLength(1); lv2++)
                    if (MapScheme[lv1, lv2] == Condition)
                        return true;
            return false;
        }
        public static void CheckMapX(in byte[] Models, in Image[] images)
        {
            for (byte i = 0; i < Models.Length; i++)
                if (CheckMap(Models[i]))
                    AnyShow(images[i]);
        }
        public static void ChangeMapToVoid(in byte Condition)
        {
            for (byte i = 0; i < MapScheme.GetLength(0); i++)
                for (byte j = 0; j < MapScheme.GetLength(1); j++)
                    if (MapScheme[i, j] == Condition)
                        MapScheme[i, j] = 0;
        }
        public static void ChangeMapToVoid(in int Row, in int Col, params Image[] Imgs)
        {
            foreach (Image img in Imgs)
                if ((Bits(img.GetValue(Grid.RowProperty)) == Row) && (Bits(img.GetValue(Grid.ColumnProperty)) == Col))
                {
                    AnyHide(img);
                    MapScheme[Row, Col] = 0;
                }
        }
        public static void ChangeMap(in byte model, params byte[] conditions)
        {
            for (byte no = 0; no < conditions.Length; no++)
                for (byte i = 0; i < MapScheme.GetLength(0); i++)
                    for (byte j = 0; j < MapScheme.GetLength(1); j++)
                        if (MapScheme[i, j] == conditions[no])
                            MapScheme[i, j] = model;
        }
        public static void ChangeMapToWall(in byte Condition)
        {
            for (byte i = 0; i < MapScheme.GetLength(0); i++)
                for (byte j = 0; j < MapScheme.GetLength(1); j++)
                    if (MapScheme[i, j] == Condition)
                        MapScheme[i, j] = 1;
        }

